let randomNumber=parseInt(100*Math.random()+1);const submit=document.querySelector("#subt"),userInput=document.querySelector("#guessField"),guessSlot=document.querySelector(".guesses"),remaining=document.querySelector(".lastResult"),startOver=document.querySelector(".resultParas"),lowOrHi=document.querySelector(".lowOrHi"),p=document.createElement("p");let previousGuesses=[],numGuesses=1,playGame=!0;function validateGuess(e){isNaN(e)?alert("Please enter a valid number"):e<1?alert("Please enter a number greater than 1!"):e>100?alert("Please enter a number less than 500!"):(previousGuesses.push(e),11===numGuesses?(displayGuesses(e),displayMessage("Game Over! Number was "+randomNumber),endGame()):(displayGuesses(e),checkGuess(e)))}function checkGuess(e){e===randomNumber?(displayMessage("You guessed correctly!"),endGame()):e<randomNumber?displayMessage("Too low! Try again!"):e>randomNumber&&displayMessage("Too High! Try again!")}function displayGuesses(e){userInput.value="",guessSlot.innerHTML+=e+"  ",numGuesses++,remaining.innerHTML=11-numGuesses+"  "}function displayMessage(e){lowOrHi.innerHTML=`<h1>${e}</h1>`}function endGame(){userInput.value="",userInput.setAttribute("disabled",""),p.classList.add("button"),p.innerHTML='<h1 id="newGame">Start New Game</h1>',startOver.appendChild(p),playGame=!1,newGame()}function newGame(){document.querySelector("#newGame").addEventListener("click",(function(){randomNumber=parseInt(100*Math.random()+1),previousGuesses=[],numGuesses=1,guessSlot.innerHTML="",lowOrHi.innerHTML="",remaining.innerHTML=11-numGuesses+"  ",userInput.removeAttribute("disabled"),startOver.removeChild(p),playGame=!0}))}playGame&&subt.addEventListener("click",(function(e){e.preventDefault();validateGuess(parseInt(userInput.value))}));